# Remove generated data from the previous run and re-create setup files/library mappings
reset_run()
{
  files_to_remove=(xelab.pb xsim.jou xvhdl.log xvlog.log compile.log elaborate.log simulate.log xelab.log xsim.log run.log xvhdl.pb xvlog.pb xsimtop.wdb xsim.dir)
  for (( i=0; i<${#files_to_remove[*]}; i++ )); do
    file="${files_to_remove[i]}"
    if [[ -e $file ]]; then
      rm -rf $file
    fi
  done
}

# Main steps
run()
{
  setup $1 $2
  compile
  elaborate
  simulate
}


# RUN_STEP: <compile>
compile()
{
  # Command line options
  opts_ver="-m64 --relax -L work"
  opts_vhd="-m64 --relax"

  # Compile design files
  xvlog $opts_ver -prj vlog.prj 2>&1 | tee compile.log
  xvhdl $opts_vhd -prj vhdl.prj 2>&1 | tee compile.log

}

# RUN_STEP: <elaborate>
elaborate()
{
  #xsc -cc gcc -link $XSIM_OBJECTS -o xsimtop

  CONNECTALDIR="/home/jamey/connectal"
  VIVADODIR="/scratch/Xilinx/Vivado/2015.4"
  XSC_CFLAGS="-Wa,-W -fPIC -m64  -I$VIVADODIR/data/xsim/include -DSYSTEM_VERILOG -DBOARD_xsim -I$CONNECTALDIR/cpp -I$CONNECTALDIR -I../../xsim/jni -I$CONNECTALDIR/"
  XSIM_GENERATED_CFILES="XsimMsgIndication XsimMsgRequest GeneratedCppCallbacks"
  XSIM_CFILES="transportSocket transportHardware transportXsim XsimTop poller sock_utils portalPrintf portal BsimDma BsimCtrl"
  XSIM_OBJECTS=""
  mkdir -p xsim.dir/xsc
  for f in $XSIM_GENERATED_CFILES; do
      if [ -f ../../xsim/jni/$f.c ] ; then
	  gcc -c $XSC_CFLAGS -O1 -o xsim.dir/xsc/$f.o ../../xsim/jni/$f.c
      else
	  gcc -c $XSC_CFLAGS -O1 -o xsim.dir/xsc/$f.o ../../xsim/jni/$f.cpp
      fi
      XSIM_OBJECTS="$XSIM_OBJECTS xsim.dir/xsc/$f.o"
  done
  for f in $XSIM_CFILES; do
      if [ -f $CONNECTALDIR/cpp/$f.c ] ; then
	  gcc -c $XSC_CFLAGS -O1 -o xsim.dir/xsc/$f.o $CONNECTALDIR/cpp/$f.c
      else
	  gcc -c $XSC_CFLAGS -O1 -o xsim.dir/xsc/$f.o $CONNECTALDIR/cpp/$f.cpp
      fi
      XSIM_OBJECTS="$XSIM_OBJECTS -i xsim.dir/xsc/$f.o"
  done

  echo "Calling xsc -link $XSIM_OBJECTS"
  xsc -v -cc gcc -link $XSIM_OBJECTS -o xsimtop

  xelabopts="-cc gcc -timescale 1ns/1ps"
  echo "xelab"
  xelab $xelabopts -L work work.XsimSource work.XsimDmaReadWrite work.axi_ethernet_0 work.mkMemServerRequestInput work.mkMemServerIndicationOutput work.mkAxiEthTestIndicationOutput work.mkAxiEthTestRequestInput work.xsimtop bsvlib.RegFile --relax --debug typical --mt auto -wto e00b451fffb34896b5b53836d3893dbf -m64 -L work -L fifo_generator_v13_0_1 -L blk_mem_gen_v8_3_1 -L lib_bmg_v1_0_3 -L axi_lite_ipif_v3_0_3 -L axi_ethernet_buffer_v2_0_10 -L bsvlib -L work -L xbip_utils_v3_0_5 -L xbip_pipe_v3_0_1 -L xbip_bram18k_v3_0_1 -L mult_gen_v12_0_10 -L tri_mode_ethernet_mac_v9_0_3 -L gig_ethernet_pcs_pma_v15_1_1 -L lib_pkg_v1_0_2 -L lib_fifo_v1_0_4 -L lib_srl_fifo_v1_0_2 -L lib_cdc_v1_0_2 -L axi_datamover_v5_1_9 -L axi_sg_v4_1_2 -L axi_dma_v7_1_8 -L unisims_ver -L unimacro_ver -L secureip --snapshot xsimtop work.glbl work.axi_dma_0 -L work  -log elaborate.log -sv_lib xsimtop
}

# RUN_STEP: <simulate>
simulate()
{
  xsim xsimtop -key {Behavioral:sim_1:Functional:xsimtop} -tclbatch cmd.tcl -log simulate.log
}
